import React from "react";
import "./App.css";
import { Matches } from "./Matches";
import { createApiClient, Match } from "./api";

export type AppState = {
  matches?: Match[];
  search: string;
};

const api = createApiClient();
const App = () => {
  const [search, setSearch] = React.useState<string>("");
  const [ListApproved, setListApproved] = React.useState<String[]>([]);
  const [countApproved, setCountApproved] = React.useState<Number>(0);
  const [matches, setMatches] = React.useState<Match[]>([]);
  React.useEffect(() => {
    async function fetchMatches() {
      setMatches(await api.getMatches());
    }
    fetchMatches();
  }, []);
  let searchDebounce: any;
  const onSearch = (val: string, newPage?: number) => {
    clearTimeout(searchDebounce);
    searchDebounce = setTimeout(async () => {
      setSearch(val);
    }, 300);
  };  

  const aprovalCounter = (id : String , number : Number) => {
    if(ListApproved.length == 0 && number > 0){
      setListApproved(prevArray => [...prevArray, id])
      setCountApproved(+countApproved + +number)
    }
    else {
      let findIdInList = ListApproved.find(x => x == id)
      console.log(findIdInList)
      if(findIdInList  == null ){
        setListApproved(prevArray => [...prevArray, id])
        setCountApproved(+countApproved + +number)
      }
    }
  }

  return (
    <main>
      <h1>Matches List</h1>
      {console.log(countApproved)}
      <header>
        <input
          type="search"
          placeholder="Search..."
          onChange={(e) => onSearch(e.target.value)}
        />
      </header>
      {matches ? (
        <div className="results">Showing {matches.length} results</div>
      ) : null}
      {matches ? (
        <Matches matches={matches} search={search} countApproved={countApproved} callback={ (id , number) => aprovalCounter(id , number) }/>
      ) : (
        <h2>Loading...</h2>
      )}
    </main>
  );
};
export default App;
